sys_variable_value.value:
(function execute(inputs, outputs) {
  // ... code ...

  var sysIDList = inputs.sysIDList;

  try{

    var listArray = sysIDList.split(',');
    var newListCSV = '';

    for(i = 0; i < listArray.length; i++){

      if(listArray[i] == '' || newListCSV.includes(listArray[i])){

      }
      else{

        newListCSV += listArray[i] + ','

      }

    }

    newListCSV = newListCSV.slice(0,-1);

    outputs.updatedsyslist = newListCSV;
  }
  catch(e){
    outputs.updatedsyslist = sysIDList;
  }

})(inputs, outputs);

